package java_package;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =21;
		
		if(a%2 == 0)
		{
			System.out.println("The number is even "+a);
		}
		
		else
		{
			System.out.println("The number is odd "+a);
		}
	}

}
